SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION fleury_tipo_pessoa_fisica (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_pessoa_w	varchar(2);
qt_idade_w		bigint;


BEGIN

select	obter_idade(pf.dt_nascimento, clock_timestamp(), 'A')
into STRICT	qt_idade_w
from	pessoa_fisica pf
where	pf.cd_pessoa_fisica 	= cd_pessoa_fisica_p;

if (qt_idade_w >= 14) then
	ie_tipo_pessoa_w := 'A';
else
	ie_tipo_pessoa_w := 'C';
end if;

return	ie_tipo_pessoa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
