CREATE OR REPLACE FUNCTION fnc_consulta_bact (cd_bacteria_p bigint, nr_atendimento_p bigint, cd_material_exame_p text) RETURNS varchar AS $body$
DECLARE


  retorno_bacter_w varchar(1) := '0';


BEGIN

     select '1'
    into STRICT retorno_bacter_w
    from cpoe_procedimento
   where nr_atendimento = nr_atendimento_p
     and cd_microorganismo <> cd_bacteria_p
     and cd_material_exame = cd_material_exame_p
     and to_date(dt_inicio, 'dd/mm/yyyy') >= to_date(clock_timestamp(), 'dd/mm/yyyy')  LIMIT 1;
  
  return retorno_bacter_w;
  exception
  when no_data_found then
  return 0;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
