CREATE OR REPLACE FUNCTION fob_obter_nf_compl_hist ( ds_texto text ) RETURNS bigint AS $body$
DECLARE


ds_retorno_w bigint;
ds_temp_w varchar(100);
nr_espaco_w bigint;


BEGIN

select
case instr(substr(ds_texto,instr(ds_texto,'NF ',1,1)),' ',1,2) when 0 then length(trim(both ds_texto)) else instr(substr(ds_texto,instr(ds_texto,'NF ',1,1)),' ',1,2) end
into STRICT nr_espaco_w
;

select
CASE WHEN instr(ds_texto,'NF ',1,1)=0 THEN ''  ELSE replace(elimina_caracteres_especiais(substr(ds_texto,instr(ds_texto,'NF ',1,1),nr_espaco_w)),'NF','') END
into STRICT ds_temp_w
;

select
CASE WHEN obter_se_somente_numero(ds_temp_w)='S' THEN (ds_temp_w)::numeric   ELSE 0 END
into STRICT ds_retorno_w
;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
