CREATE OR REPLACE FUNCTION formatar_dosage_md ( qt_valor_p bigint ) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(255);

BEGIN

  ds_retorno_w := to_char(qt_valor_p);

  if (substr(ds_retorno_w, 1, 1) in (',', '.')) then   
	ds_retorno_w := '0' || ds_retorno_w;
  end if;

  return ds_retorno_w;
end;
$body$
