CREATE OR REPLACE FUNCTION formatar_hora ( dt_inicial_p timestamp, nr_inicial_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_dif_w		varchar(15);
qt_hora_w		bigint;
qt_min_w		bigint;
qt_seg_w		bigint;


BEGIN

ds_dif_w		:= '00000:00:00';

qt_Seg_w	:= trunc(coalesce(nr_inicial_p,(dt_inicial_p)::numeric ) * 86400);
qt_hora_w	:= trunc(qt_seg_w / 3600);
qt_seg_w	:= qt_seg_w - (qt_hora_w * 3600);
qt_min_w	:= trunc(qt_seg_w / 60);
qt_seg_w	:= qt_seg_w - (qt_min_w * 60);
ds_dif_w	:= to_char(coalesce(qt_hora_w,0),'FM999900') || ':' || to_char(coalesce(qt_min_w,0),'FM00') || ':' || to_char(coalesce(qt_seg_w,0),'FM00');

Return ds_dif_w;

end;
$body$
