CREATE OR REPLACE FUNCTION formata_campo_titulo_relat ( cd_conta_contabil_p text, cd_centro_custo_p bigint, cd_estabelecimento_p bigint, ds_campo_p text, ie_campo_p text, ds_formatacao_p text) RETURNS varchar AS $body$
DECLARE


/*
Ie_campo_p = 	'V' - Varchar2
		'N' - Number
Criada por Anderson 03/08/2007 para atender uma necessidade espec√≠fica com base na OS 63821
*/
ds_retorno_w	varchar(2000);


BEGIN

if (cd_conta_contabil_p IS NOT NULL AND cd_conta_contabil_p::text <> '') or (cd_centro_custo_p IS NOT NULL AND cd_centro_custo_p::text <> '') then

	if (ie_campo_p = 'V') then

		select	coalesce(CASE WHEN substr(obter_dados_conta_contabil(cd_conta_contabil_p, cd_estabelecimento_p, 'T'),1,1)='T' THEN   '#' ||				ds_campo_p || '#' || ds_formatacao_p || '#P'  ELSE CASE WHEN substr(ctb_obter_dados_centro_custo(cd_centro_custo_p,'T'),1,1)='T' THEN  '#' || ds_campo_p || '#' ||				ds_formatacao_p || '#P'  ELSE ds_campo_p END  END , ds_campo_p)
		into STRICT	ds_retorno_w
		;

	elsif (ie_campo_p = 'N') then

		select	coalesce(CASE WHEN substr(obter_dados_conta_contabil(cd_conta_contabil_p, cd_estabelecimento_p, 'T'),1,1)='T' THEN   '#' ||				substr(campo_mascara_virgula(ds_campo_p),1,30) || '#' || ds_formatacao_p || '#P'  ELSE CASE WHEN substr(ctb_obter_dados_centro_custo(cd_centro_custo_p,'T'),1,1)='T' THEN  '#' || substr(campo_mascara_virgula(ds_campo_p),1,30) ||				'#' || ds_formatacao_p || '#P'  ELSE substr(campo_mascara_virgula(ds_campo_p),1,30) END  END , substr(campo_mascara_virgula(ds_campo_p),1,30))
		into STRICT	ds_retorno_w
		;

	end if;
else
	if (ie_campo_p = 'V') then

		select	'#' || ds_campo_p || '#' || ds_formatacao_p || '#P'
		into STRICT	ds_retorno_w
		;

	elsif (ie_campo_p = 'N') then

		select	'#' || substr(campo_mascara_virgula(ds_campo_p),1,30) || '#' || ds_formatacao_p || '#P'
		into STRICT	ds_retorno_w
		;
	end if;
end if;

return ds_retorno_w;

end;
$body$
