CREATE OR REPLACE FUNCTION formata_espaco_entre_campos (ds_valor_campo_p text, nr_espacos_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
ds_espacos_w		varchar(255);
i			integer	:= 1;


BEGIN

for	i in 1 .. nr_espacos_p loop
	ds_espacos_w	:= ds_espacos_w || ' ';
end loop;

if (length(ds_valor_campo_p) > 0) then
	for	i in 1 .. length(ds_valor_campo_p) loop
		ds_retorno_w	:= ds_retorno_w || substr(ds_valor_campo_p,i,1) || ds_espacos_w || '|' || ds_espacos_w;
	end loop;
end if;

ds_retorno_w	:= substr(rpad(ds_retorno_w,500,ds_espacos_w || ' |' || ds_espacos_w),1,500);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
