CREATE OR REPLACE FUNCTION formata_hora ( ds_hora_p text) RETURNS varchar AS $body$
DECLARE

ds_hora_w   	varchar(5);
ds_horaaux_w  	varchar(5);
ds_minutos_w  	varchar(5);


BEGIN
   ds_horaaux_w := substr(ds_hora_p,1,position(':' in ds_hora_p)-1);

if (length(ds_horaaux_w) = 1 ) then
	ds_horaaux_w := '0' || ds_horaaux_w;
end if;

ds_minutos_w := substr(ds_hora_p,position(':' in ds_hora_p)+1,2);

if (length(ds_minutos_w) = 1 ) then
	ds_minutos_w := '0' || ds_minutos_w;
end if;

ds_hora_w := ds_horaaux_w ||':'|| ds_minutos_w;

return ds_hora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
