CREATE OR REPLACE FUNCTION format_array_json ( nm_campo_p text, ds_valor_p text, ie_tipo_p bigint) RETURNS text AS $body$
DECLARE


/* IE_TIPO_P
1 = Atributo x Valor
2 = Array
*/
ds_retorno_w    text;


BEGIN

if (ie_tipo_p = 1) then
    ds_retorno_w := '"' || nm_campo_p || '":' || '"' || ds_valor_p || '", ';
else
    ds_retorno_w := '"' || nm_campo_p || '":'  || ds_valor_p || ', ';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
