CREATE OR REPLACE FUNCTION fornec_consig_maior_saldo ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, dt_mesano_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(14);


BEGIN


begin
select	cd_fornecedor
into STRICT	ds_retorno_w
from	fornecedor_mat_consignado
where	cd_estabelecimento		= cd_estabelecimento_p
and	cd_local_estoque	 	= cd_local_estoque_p
and	cd_material		= cd_material_p
and	dt_mesano_referencia	= trunc(dt_mesano_referencia_p,'mm')
and	qt_estoque = (
	SELECT	max(qt_estoque)
	from	fornecedor_mat_consignado
	where	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_local_estoque		= cd_local_estoque_p
	and	cd_material		= cd_material_p
	and	dt_mesano_referencia	= trunc(dt_mesano_referencia_p, 'mm'));
exception when no_data_found then
	ds_retorno_w	:= '0';
end;

return coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
