SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gedipa_consiste_etiq_processo ( nr_seq_processo_p bigint, nr_seq_area_prep_p bigint, nr_etiqueta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255) 	:= null;
ie_existe_w		varchar(1)	:= 'S';
ie_possui_etiqueta_w	varchar(1);

/*
ie_opcao_p
V - Verifica se existe etiqueta vinculada ao processo
C - Consiste a etiqueta informada
*/
BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_possui_etiqueta_w
from	lp_individual
where	nr_seq_processo	 	= nr_seq_processo_p;
--and	nvl(nr_seq_area_prep,0)	= nvl(nr_seq_area_prep_p,0);
if (ie_possui_etiqueta_w = 'N') then
   select	coalesce(max('S'),'N')
    into STRICT	ie_possui_etiqueta_w
    from	lp_processo_desdobrado
    where	nr_seq_processo	 	= nr_seq_processo_p;
end if;

if (ie_opcao_p = 'C') and (ie_possui_etiqueta_w = 'S') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_existe_w
	from	lp_individual
	where	nr_seq_processo	 	= nr_seq_processo_p
	--and	nvl(nr_seq_area_prep,0)	= nvl(nr_seq_area_prep_p,0)
	and	nr_etiqueta		= nr_etiqueta_p;

    if (ie_existe_w = 'N') then
        select	coalesce(max('S'),'N')
        into STRICT	ie_existe_w
        from	lp_processo_desdobrado
        where	nr_seq_processo	 	= nr_seq_processo_p
        --and	nvl(nr_seq_area_prep,0)	= nvl(nr_seq_area_prep_p,0)
        and (9 || lpad(NR_SEQ_LP_INDIVIDUAL,12,0))		= nr_etiqueta_p;

    end if;
end if;

if (ie_opcao_p = 'V') then
	ds_retorno_w := ie_possui_etiqueta_w;
elsif (ie_opcao_p = 'C') and (ie_existe_w = 'N') then
	ds_retorno_w := Wheb_mensagem_pck.get_texto(308436); --'A etiqueta informada não está vinculada ao processo!';
end if;

return	substr(ds_retorno_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION gedipa_consiste_etiq_processo ( nr_seq_processo_p bigint, nr_seq_area_prep_p bigint, nr_etiqueta_p bigint, ie_opcao_p text) FROM PUBLIC;

