CREATE OR REPLACE FUNCTION gedipa_obter_dados_soluc ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1000);
ie_bomba_w		varchar(1);
ds_bomba_infusao_w 	varchar(255);
qt_solucao_total_w	double precision;

expressao1_w	varchar(255) := obter_desc_expressao_idioma(284349, null, wheb_usuario_pck.get_nr_seq_idioma);--Bomba de infus√£o:
expressao2_w	varchar(255) := obter_desc_expressao_idioma(302287, null, wheb_usuario_pck.get_nr_seq_idioma);--Volume total
BEGIN
if (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '')  and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select 	max(ie_bomba_infusao),
		max(qt_solucao_total)
	into STRICT	ie_bomba_w,
		qt_solucao_total_w
	from	prescr_solucao
	where	nr_prescricao		=	nr_prescricao_p
	and	nr_seq_solucao		=	nr_seq_solucao_p;

	select 	max(ds_valor_dominio)
	into STRICT	ds_bomba_infusao_w
	from   	valor_dominio
	where  	cd_dominio = 1537
	and    	vl_dominio = ie_bomba_w;

	ds_retorno_w := substr(expressao1_w || ' '  || ds_bomba_infusao_w ||  '     -    ' || expressao2_w || ': ' || qt_solucao_total_w,1,100) || ' ml';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
