CREATE OR REPLACE FUNCTION gedipa_obter_dose_preparo ( nr_prescricao_p prescr_medica.nr_prescricao%type) RETURNS varchar AS $body$
DECLARE


qt_dose_w			prescr_material.qt_dose%type;


BEGIN
qt_dose_w := 0;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
    select	coalesce(max(c.qt_dose),0)
    into STRICT	qt_dose_w
    from 	gedipa_estoque_cabine a,
            material b,
            prescr_material c
    where 	a.cd_material   =   b.cd_material
    and		b.cd_material   =   c.cd_material
    and     c.nr_prescricao =	nr_prescricao_p
    and 	a.nr_seq_area_prep  =   84
    and		qt_estoque  >    0
    and 	b.ie_tipo_material  =   '1';
end if;

return qt_dose_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
