CREATE OR REPLACE FUNCTION gedipa_obter_se_regra_lote (nr_seq_processo_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_regra_w	varchar(1);
ie_Possui_mat_w		varchar(1);


BEGIN


select	coalesce(max('S'),'N')
into STRICT	ie_Possui_mat_w
from	prescr_mat_hor
where	cd_material						= cd_material_p
and		nr_seq_processo				= nr_seq_processo_p;

if (ie_Possui_mat_w = 'N') then
	ie_possui_regra_w := 'S';
else

	select 	coalesce(max('S'),'N')
	into STRICT	ie_possui_regra_w
	from	adep_area_prep e,
			prescr_mat_hor d
	where	d.nr_seq_area_prep	= e.nr_sequencia
	and		coalesce(e.ie_adm_lote_fornec,'N')	= 'S'
	and		d.nr_seq_processo				= nr_seq_processo_p
	and		cd_material						= cd_material_p;

end if;

return	ie_possui_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
