CREATE OR REPLACE FUNCTION gel_obter_data_antecipacao_dt (nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w		timestamp;
nr_sequencia_w		varchar(80);


BEGIN

select	max(to_char(nr_sequencia))
into STRICT	nr_sequencia_w
from	prescr_proc_pedido_entrega
where	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescricao_p
and	coalesce(dt_confirmacao::text, '') = ''
and	coalesce(dt_cancelamento::text, '') = '';

select  max(dt_antecipacao)
into STRICT	ds_retorno_w
from	prescr_proc_pedido_entrega
where	nr_sequencia = nr_sequencia_w;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
