CREATE OR REPLACE FUNCTION gel_obter_data_antecipacao (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


dt_antecipacao_w			varchar(80);
nr_sequencia_w			varchar(80);


BEGIN

select	max(to_char(nr_sequencia))
into STRICT	nr_sequencia_w
from	prescr_proc_pedido_entrega
where	nr_prescricao = nr_prescricao_p
and	nr_seq_prescricao = nr_seq_prescricao_p
and	coalesce(dt_confirmacao::text, '') = ''
and	coalesce(dt_cancelamento::text, '') = '';

select  max(to_char(dt_antecipacao,'dd/mm/yyyy hh24:mi:ss'))
into STRICT	dt_antecipacao_w
from	prescr_proc_pedido_entrega
where	nr_sequencia = nr_sequencia_w;

if (ie_tipo_p = 'N') then
   return	nr_sequencia_w;
elsif (ie_tipo_p = 'D') then
   return	dt_antecipacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
