CREATE OR REPLACE FUNCTION gel_obter_prescr_envelope (nr_seq_envelope_p bigint) RETURNS bigint AS $body$
DECLARE


nr_prescricao_w		bigint;


BEGIN

select	max(a.nr_prescricao)
into STRICT	nr_prescricao_w
from    envelope_laudo_item d,
   	prescr_procedimento b,
	prescr_medica a
where	a.nr_prescricao = b.nr_prescricao
and     b.nr_prescricao = d.nr_prescricao
and     b.nr_sequencia = d.nr_seq_prescr
and	d.nr_seq_envelope = nr_seq_envelope_p;

return	nr_prescricao_w;

end;
