SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gel_obter_prescr_item_env ( nr_seq_item_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_prescricao_w			integer;
nr_prescricao_w				bigint;
nr_seq_laudo_w				bigint;
nr_seq_result_lab_w			bigint;
ie_anatomia_w				varchar(1) := 'N';
nr_seq_exame_w				bigint;
nr_prescricao_ww				bigint;
nr_seq_prescr_w				integer;


BEGIN

select	max(nr_seq_laudo),
	max(nr_seq_result_lab),
	max(nr_prescricao),
	max(nr_seq_prescr)
into STRICT	nr_seq_laudo_w,
	nr_seq_result_lab_w,
	nr_prescricao_ww,
	nr_seq_prescr_w
from	envelope_laudo_item
where	nr_sequencia = nr_seq_item_p;

select	max(nr_seq_exame)
into STRICT	nr_seq_exame_w
from	prescr_procedimento
where	nr_prescricao = nr_prescricao_ww
and	nr_sequencia = nr_seq_prescr_w;

select 	max(coalesce(ie_anatomia_patologica,'N'))
into STRICT	ie_anatomia_w
from	exame_laboratorio
where 	nr_seq_exame = nr_seq_exame_w;

if (nr_seq_result_lab_w IS NOT NULL AND nr_seq_result_lab_w::text <> '') and (ie_anatomia_w <> 'S') then
	select	max(nr_Seq_prescricao),
		max(nr_prescricao)
	into STRICT	nr_seq_prescricao_w,
		nr_prescricao_w
	from	result_laboratorio
	where	nr_sequencia = nr_seq_result_lab_w;
else
	select	max(nr_Seq_prescricao),
		max(nr_prescricao)
	into STRICT	nr_seq_prescricao_w,
		nr_prescricao_w
	from	laudo_paciente
	where	nr_sequencia = nr_seq_laudo_w;
end if;

if (ie_opcao_p = 'S') then
	return	nr_seq_prescricao_w;
else
	return	nr_prescricao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
