CREATE OR REPLACE FUNCTION gel_obter_status_envelope ( nr_seq_envelope_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_status_w		varchar(250);
nr_seq_status_w		bigint;
ds_retorno_w		varchar(250);


BEGIN

if (nr_seq_envelope_p IS NOT NULL AND nr_seq_envelope_p::text <> '')then
	select	nr_seq_status_envelope,
		SUBSTR(obter_valor_dominio(3221,nr_seq_status_envelope),1,100)
	into STRICT	nr_seq_status_w,
		ds_status_w
	from	envelope_laudo
	where	nr_sequencia = nr_seq_envelope_p;
else
    select	SUBSTR(obter_valor_dominio(3221,45),1,100)
    into STRICT    ds_status_w
;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := to_char(nr_seq_status_w);
else
	ds_retorno_w := ds_status_w;
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
