CREATE OR REPLACE FUNCTION gel_se_registra_dados (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
nr_seq_forma_laudo_w	bigint;


BEGIN


select	coalesce(max(nr_seq_forma_laudo),0)
into STRICT	nr_seq_forma_laudo_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	forma_entrega_laudo
where	ie_situacao = 'A'
and	coalesce(ie_registrar_dados_entrega,'N') = 'S'
and	nr_sequencia = nr_seq_forma_laudo_w;

return	ie_retorno_w;

end;
