CREATE OR REPLACE FUNCTION generate_uuid () RETURNS varchar AS $body$
DECLARE

  cod_uuid varchar(40);

BEGIN
  select regexp_replace(upper(uuid_generate_v4()), '([A-F0-9]{8})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{4})([A-F0-9]{12})', '\1-\2-\3-\4-\5')
    into STRICT cod_uuid
;
  return cod_uuid;
END;
