CREATE OR REPLACE FUNCTION gerar_alt_versao_release_atx ( cd_versao_p text) RETURNS bigint AS $body$
DECLARE


nr_release_w	versao_alteracao_script.nr_release%type;
BEGIN

	select  count(*)
	into STRICT    nr_release_w
	from    versao_alteracao_script
	where   cd_versao = cd_versao_p
	and     coalesce(nr_release::text, '') = '';

	if nr_release_w > 0 then

		select  max(coalesce(nr_release, 0)) + 1
		into STRICT    nr_release_w
		from    versao_alteracao_script
		where   cd_versao = cd_versao_p;

		update  versao_alteracao_script
		set     nr_release = nr_release_w
		where   cd_versao = cd_versao_p
		and     coalesce(nr_release::text, '') = '';

		commit;

	else

		select  max(nr_release)
		into STRICT    nr_release_w
		from    versao_alteracao_script
		where   cd_versao = cd_versao_p;

		if (coalesce(nr_release_w::text, '') = '') then
			nr_release_w := 0;
		end if;

	end if;

	return nr_release_w;
end;
