CREATE OR REPLACE FUNCTION gerar_data_seguimento (nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE


qt_registro_w	integer;
dt_aux_w	timestamp;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	can_ficha_seguimento
where	NR_SEQ_FICHA_ADMISSAO = nr_sequencia_p;

if (qt_registro_w = 0) then
	begin

	select	PKG_DATE_UTILS.ADD_MONTH(dt_consulta, 24,0)
	into STRICT	dt_aux_w
	from	can_ficha_admissao
	where	nr_sequencia = nr_sequencia_p;

	end;
elsif (qt_registro_w > 0)	and (qt_registro_w < 11)	then
	begin

	select	PKG_DATE_UTILS.ADD_MONTH(max(dt_seguimento),12,0)
	into STRICT	dt_aux_w
	from	can_ficha_seguimento
	where	NR_SEQ_FICHA_ADMISSAO = nr_sequencia_p;

	end;
elsif (qt_registro_w > 10) then
	begin

	select	PKG_DATE_UTILS.ADD_MONTH(max(dt_seguimento),60,0)
	into STRICT	dt_aux_w
	from	can_ficha_seguimento
	where	NR_SEQ_FICHA_ADMISSAO = nr_sequencia_p;

	end;
end if;

return	dt_aux_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION gerar_data_seguimento (nr_sequencia_p bigint) FROM PUBLIC;

