CREATE OR REPLACE FUNCTION gerar_identificao_imagem (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_atendimento_w		bigint;
cd_PrestadorOperadora_w		varchar(14);

ds_retorno_w			varchar(40);


BEGIN
nr_atendimento_w := nr_atendimento_p;

cd_PrestadorOperadora_w := Obter_Param_Usuario(-2260, 5, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, cd_PrestadorOperadora_w);

if coalesce(cd_PrestadorOperadora_w::text, '') = '' then
	cd_PrestadorOperadora_w := '_';
end if;

ds_retorno_w := LPAD(cd_PrestadorOperadora_w, 14, '_') || LPAD(coalesce(Obter_guia_atend(nr_atendimento_w,
								obter_convenio_atendimento(nr_atendimento_w),
								Obter_categoria_atendimento(nr_atendimento_w)),'_'), 20, '_');

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
