CREATE OR REPLACE FUNCTION gerar_porcent_os_duvida_sup ( dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS double precision AS $body$
DECLARE


qt_total_os_w	bigint;
qt_os_duv_w		bigint;
qt_porcent_w	double precision := 0;


BEGIN

select	count(*)
into STRICT	qt_total_os_w
from	man_ordem_servico a
where	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
and 	a.ie_status_ordem in ('1','2')
and		a.nr_seq_grupo_sup = 63
and		a.dt_ordem_servico between dt_inicio_p and dt_fim_p;

select	count(*)
into STRICT	qt_os_duv_w
from	man_ordem_servico a
where	(a.nr_seq_grupo_sup IS NOT NULL AND a.nr_seq_grupo_sup::text <> '')
and 	a.ie_status_ordem in ('1','2')
and		a.nr_seq_grupo_sup = 63
and		a.ie_classificacao = 'D'
and		a.dt_ordem_servico between dt_inicio_p and dt_fim_p;

if	(qt_total_os_w <> 0 AND qt_os_duv_w <> 0) then
	qt_porcent_w := (qt_os_duv_w/qt_total_os_w) * 100;
end if;

return	qt_porcent_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION gerar_porcent_os_duvida_sup ( dt_inicio_p timestamp, dt_fim_p timestamp) FROM PUBLIC;

