SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION gerar_texto_assinat_relat ( nr_seq_assinatura_p bigint) RETURNS varchar AS $body$
DECLARE


ds_texto_w varchar(255);
ds_status_w varchar(255);


BEGIN
ds_texto_w := '';

if (nr_seq_assinatura_p IS NOT NULL AND nr_seq_assinatura_p::text <> '') then

    select  obter_desc_expressao(786459)/*Documento assinado digitalmente....*/
 ||
            ' '  || obter_desc_expressao(296059) /* por */|| ' ' || substr(obter_nome_pf(CD_PESSOA_FISICA),1,39)    ||
            ', ' || obter_desc_expressao(618483) /* as*/|| ' ' || to_char(dt_assinatura, 'hh24:MI')             ||
            coalesce(obter_utc(dt_assinatura),'') ||
            ' '  || obter_desc_expressao(746911) /*de*/|| ' ' || to_char(dt_assinatura, 'DD/MM/YYYY')
    into STRICT    ds_texto_w
    from    tasy_assinatura_digital
    where   nr_sequencia = nr_seq_assinatura_p
    and (dt_assinatura IS NOT NULL AND dt_assinatura::text <> '');

    ds_texto_w := SUBSTR(ds_texto_w,1,230);

    ds_texto_w := ds_texto_w || ' - ' || SUBSTR(obter_desc_validacao_assinat(nr_seq_assinatura_p),1,20);

end if;

return ds_texto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
