CREATE OR REPLACE FUNCTION gera_pdf_convenio ( cd_convenio_p bigint, ie_tipo_convenio_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'S';

BEGIN

select  CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_retorno_w
from 	regra_ger_arq_lab
where	ie_situacao = 'A';

if (ie_retorno_w = 'N') then
	begin
	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from 	regra_ger_arq_lab
	where	coalesce(cd_convenio,cd_convenio_p) 		= cd_convenio_p
	and	coalesce(ie_tipo_convenio,ie_tipo_convenio_p) 	= ie_tipo_convenio_p
	and	ie_situacao 		= 'A';
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
