CREATE OR REPLACE FUNCTION ger_carga_obter_total_processo (nr_seq_carga_p bigint, ie_condicao_p text, ie_status_p text) RETURNS varchar AS $body$
DECLARE

	
nr_seq_carga_arq_w	ger_carga_arq.nr_sequencia%type;
qt_total_acao_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	ger_tipo_carga_arq a,
	ger_carga_arq b
where	a.nr_sequencia	= b.nr_seq_tipo_arq
and	b.nr_seq_carga	= nr_seq_carga_p
and	a.ie_principal	= 'S';
	

BEGIN

qt_total_acao_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_carga_arq_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	qt_total_acao_w := qt_total_acao_w + GER_OBTER_QT_LIDOS( nr_seq_carga_arq_w, ie_condicao_p, ie_status_p);	

end loop;
close C01;

return	qt_total_acao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
