SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION getqtetapashm ( nr_prescricao_p bigint, nr_seq_hemoterapia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_etapas_w		bigint;
qt_etapas_reg_w	bigint;


BEGIN

begin
    select	coalesce(obter_ocorrencias_horarios_rep(a.ds_horarios),1) * coalesce(a.qt_procedimento,1)
	into STRICT	nr_etapas_w
	from	prescr_procedimento a
	where	nr_prescricao = nr_prescricao_p
	and		nr_sequencia = nr_seq_hemoterapia_p;
exception when others then
	nr_etapas_w		:= 1;
end;

select 	count(1)
into STRICT	qt_etapas_reg_w
from	prescr_proc_bolsa a
where	a.nr_prescricao = nr_prescricao_p
and		a.nr_seq_procedimento = nr_seq_hemoterapia_p
and		coalesce(a.dt_cancelamento::text, '') = ''
and		not exists (	SELECT	1
						from	prescr_proc_hor b
						where	b.nr_prescricao = a.nr_prescricao
						and		b.nr_seq_procedimento = a.nr_seq_procedimento
						and		b.nr_sequencia = a.nr_seq_horario
						and		(dt_suspensao IS NOT NULL AND dt_suspensao::text <> ''));

if (qt_etapas_reg_w > nr_etapas_w) then
	nr_etapas_w	:= qt_etapas_reg_w;
end if;

return	nr_etapas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION getqtetapashm ( nr_prescricao_p bigint, nr_seq_hemoterapia_p bigint) FROM PUBLIC;

