CREATE OR REPLACE FUNCTION get_abdata_has_valid_version ( ie_tipo_import_p text, nr_versao_atual_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);

BEGIN

if (ie_tipo_import_p = 'GES') then
    begin
    select	'N'
    into STRICT    ds_retorno_w
    from	abdamed_import
    where	1 = 1
    and ie_tipo         = 'ADD'
    and	(dt_importacao IS NOT NULL AND dt_importacao::text <> '')
    and	dt_versao_atual > to_date(nr_versao_atual_p,'yyyymmdd')  LIMIT 1;
    exception
    when others then
        ds_retorno_w := 'S';
    end;
else
    begin
    select	'N'
    into STRICT    ds_retorno_w
    from	abdamed_import
    where	1 = 1
    and ie_tipo         in ('UPD','DEL')
    and	(dt_importacao IS NOT NULL AND dt_importacao::text <> '')
    and	dt_versao_atual > to_date(nr_versao_atual_p,'yyyymmdd')  LIMIT 1;
    exception
    when others then
        ds_retorno_w := 'S';
    end;
end if;

return	ds_retorno_w;

end	;
$body$
