CREATE OR REPLACE FUNCTION get_accommodation_req_date (dt_solicitacao_p timestamp, nr_seq_gestao_vaga_p bigint, ie_status_p text, nr_sequencia_p bigint) RETURNS timestamp AS $body$
DECLARE

										
request_date_w	timestamp;
approved_request_date_w timestamp;
settled_request_date_w timestamp;


BEGIN
	Select max(x.dt_atualizacao)
	into STRICT approved_request_date_w
	from gestao_vaga_hist_status x
	where x.ie_status = 'O' 
	and nr_seq_gestao_vaga = nr_seq_gestao_vaga_p;
	
	Select max(x.dt_atualizacao)
	into STRICT settled_request_date_w
	from gestao_vaga_hist_status x
	where x.ie_status = 'P' 
	and nr_seq_gestao_vaga = nr_seq_gestao_vaga_p;
	
	
	Select case ie_status
				when 'O' then dt_solicitacao_p 
				when 'P' then approved_request_date_w
				when 'F' then settled_request_date_w 
			end
	into STRICT request_date_w
	from gestao_vaga_hist_status
	Where nr_seq_gestao_vaga = nr_seq_gestao_vaga_p
    and nr_sequencia = nr_sequencia_p
	and ie_status = ie_status_p;
	
	return request_date_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
