CREATE OR REPLACE FUNCTION get_active_interface_count ( cd_dispatch_interface_p bigint, cd_return_interface_p bigint) RETURNS bigint AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	bigint;
BEGIN
	v_query := 'SELECT * FROM get_active_interface_count_atx ( ' || quote_nullable(cd_dispatch_interface_p) || ',' || quote_nullable(cd_return_interface_p) || ' )';
