CREATE OR REPLACE FUNCTION get_active_interface_count_atx ( cd_dispatch_interface_p bigint, cd_return_interface_p bigint) RETURNS bigint AS $body$
DECLARE


message_code_w				bigint;
dispatch_interface_count_w 	bigint;
return_interface_count_w 	bigint;
BEGIN

message_code_w	:= 0;
dispatch_interface_count_w  := 0;
return_interface_count_w := 0;

if (cd_return_interface_p > 0) then

		select 	count(1)
		into STRICT 	return_interface_count_w
		from 	lab_interface_ativa
		where 	nr_seq_retorno = cd_return_interface_p
		and		ie_situacao = 'A'  LIMIT 1;
end if;

if (cd_dispatch_interface_p > 0) then
		select 	count(1)
		into STRICT 	dispatch_interface_count_w
		from 	lab_interface_ativa
		where 	nr_seq_envio = cd_dispatch_interface_p
		and		ie_situacao = 'A'  LIMIT 1;
end if;

if(dispatch_interface_count_w > 0 AND return_interface_count_w > 0) then
	message_code_w	:= 339484;
elsif (dispatch_interface_count_w > 0) then
	message_code_w	:= 338053;
elsif (return_interface_count_w > 0) then
	message_code_w	:= 338054;
end if;

return	message_code_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
