SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_additional_diagnosis ( nr_atendimento_p bigint, qt_diagnosticos_p bigint) RETURNS varchar AS $body$
DECLARE


cd_doenca_w		varchar(10);
ds_retorno_w		varchar(2000) := '';


C01 CURSOR FOR
	SELECT	cd_doenca
	from	diagnostico_doenca
	where	nr_atendimento	= nr_atendimento_p
	and	ie_classificacao_doenca = 'S'
	order by dt_diagnostico desc LIMIT (qt_diagnosticos_p);


BEGIN

open c01;
	loop
	fetch c01 into
		cd_doenca_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_retorno_w := Rpad(cd_doenca_w ,6, ' ') || ds_retorno_w;

	end;
	end loop;
close c01;

ds_retorno_w := substr(ds_retorno_w, 1, length(ds_retorno_w) - 2);

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
