CREATE OR REPLACE FUNCTION get_address_econsent ( cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


ds_endereco_w			compl_pessoa_fisica.ds_endereco%type;
ds_complemento_w		compl_pessoa_fisica.ds_complemento%type;
ds_compl_end_w			compl_pessoa_fisica.ds_compl_end%type;
ds_retorno_w			varchar(255);

BEGIN

	select	max(a.ds_endereco),
			max(a.ds_complemento),
			max(a.ds_compl_end)
	into STRICT	ds_endereco_w,
			ds_complemento_w,
			ds_compl_end_w
	from	compl_pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

	if (ds_complemento_w IS NOT NULL AND ds_complemento_w::text <> '') then
		ds_retorno_w := ds_endereco_w || ' ' || ds_compl_end_w || ' ' || ds_complemento_w;
	else
		ds_retorno_w := ds_endereco_w || ' ' || ds_compl_end_w;
	end if;

	return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
