CREATE OR REPLACE FUNCTION get_admit_source_hl7 ( nr_atendimento_p bigint, nr_seq_interno_p bigint) RETURNS bigint AS $body$
DECLARE


admit_source_w	smallint := 7;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 7  ELSE 2 END
	into STRICT	admit_source_w
	from	setor_atendimento s,
		atend_paciente_unidade u
	where	s.cd_setor_atendimento = u.cd_setor_atendimento
	and	s.cd_classif_setor = '1'
	and	u.nr_atendimento = nr_atendimento_p
	and	u.nr_seq_interno < nr_seq_interno_p;
	end;
end if;
return admit_source_w;
end;
$body$
LANGUAGE PLPGSQL
