CREATE OR REPLACE FUNCTION get_altura_envergadura ( qt_altura_p bigint, ie_sexo_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w AVAL_NUTRICAO.QT_ALTURA%type;
qt_divisor_w bigint;

BEGIN
if (qt_altura_p IS NOT NULL AND qt_altura_p::text <> '') then
	begin
	if (ie_sexo_p = 'F') then
		qt_divisor_w := 1.03;
	else
		qt_divisor_w := 1.06;
	end if;
	qt_retorno_w := qt_altura_p / qt_divisor_w;
	end;
end if;
return qt_retorno_w;
end;
