CREATE OR REPLACE FUNCTION get_approve_disc_summary (nr_sequencia_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

  ds_return_w    valor_dominio.vl_dominio%TYPE := 'N';
  nr_seq_regra_w wl_regra_item.nr_seq_regra%TYPE;

BEGIN
    SELECT coalesce(Max(nr_seq_regra), 0) 
    INTO STRICT   nr_seq_regra_w 
    FROM   wl_worklist 
    WHERE  nr_seq_disc_summary = nr_sequencia_p;

    IF ( nr_seq_regra_w > 0 ) THEN 
      SELECT Obter_se_wl_liberado(cd_perfil_p, nm_usuario_p, nr_seq_regra_w) 
      INTO STRICT   ds_return_w 
;
    ELSE 
      ds_return_w := 'N';
    END IF;

    RETURN ds_return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
