CREATE OR REPLACE FUNCTION get_atend_vat_category ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, cd_convenio_p atend_categoria_convenio.cd_convenio%type, cd_categoria_p atend_categoria_convenio.cd_categoria%type) RETURNS bigint AS $body$
DECLARE

									
nr_seq_categoria_iva_w	atend_categoria_convenio.nr_seq_categoria_iva%type;


BEGIN

nr_seq_categoria_iva_w := null;

	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		select  max(acc.nr_seq_categoria_iva)
		into STRICT	nr_seq_categoria_iva_w
		from    atend_categoria_convenio acc
		where   acc.nr_atendimento = nr_atendimento_p
		and   	acc.cd_categoria = cd_categoria_p
		and   	acc.cd_convenio = cd_convenio_p;
	end if;
		
	if (coalesce(nr_seq_categoria_iva_w::text, '') = '') then
		select	max(cc.nr_seq_categoria_iva)
		into STRICT 	nr_seq_categoria_iva_w
		from 	categoria_convenio cc
		where 	cc.cd_convenio = cd_convenio_p
		and 	cc.cd_categoria = cd_categoria_p;	
	end if;

    return  nr_seq_categoria_iva_w;
	
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
