CREATE OR REPLACE FUNCTION get_attribute_expression ( nm_atributo_p text, nm_tabela_p text) RETURNS bigint AS $body$
DECLARE


CD_LABEL_W	bigint;


BEGIN

select	coalesce(CD_EXP_LABEL, CD_EXP_LABEL_GRID) INTO STRICT CD_LABEL_W
from	tabela_atributo
where	nm_tabela	= upper(nm_tabela_p)
and	nm_atributo	= upper(nm_atributo_p);

return	CD_LABEL_W;
exception
      when no_data_found then return null;
	  when too_many_rows then raise;

end	;
$body$
LANGUAGE PLPGSQL
