CREATE OR REPLACE FUNCTION get_attribute_type ( nm_tabela_p text, nm_atributo_p text ) RETURNS varchar AS $body$
DECLARE

    ie_tipo_atributo_w tabela_atributo.ie_tipo_atributo%TYPE;

BEGIN
    IF (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '' AND nm_atributo_p IS NOT NULL AND nm_atributo_p::text <> '') THEN
        SELECT
            coalesce(ie_tipo_atributo, 'function')
        INTO STRICT ie_tipo_atributo_w
        FROM
            tabela_atributo
        WHERE
                nm_tabela = nm_tabela_p
            AND nm_atributo = nm_atributo_p;

    END IF;

    RETURN ie_tipo_atributo_w;
END;
