SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_bed_request_details ( ie_status_p gestao_vaga.ie_status%TYPE, cd_setor_desejado_p gestao_vaga.cd_setor_desejado%TYPE, cd_unidade_basica_p gestao_vaga.cd_unidade_basica%TYPE, cd_unidade_compl_p gestao_vaga.cd_unidade_compl%TYPE ) RETURNS varchar AS $body$
DECLARE

    ie_status_unidade_w   unidade_atendimento.ie_status_unidade%TYPE;
    ie_reserved_w         varchar(1) := 'N';

BEGIN
    SELECT
        max(ie_status_unidade)
    INTO STRICT ie_status_unidade_w
    FROM
        unidade_atendimento
    WHERE
        cd_setor_atendimento = cd_setor_desejado_p
        AND cd_unidade_basica = cd_unidade_basica_p
        AND ( coalesce(cd_unidade_compl_p::text, '') = ''
              OR cd_unidade_compl = cd_unidade_compl_p );

/* right click Undo Bed Reservation visibility and bed reservation fields visibility */

    IF ( ie_status_p = 'R' AND ie_status_unidade_w = 'R' ) THEN
        ie_reserved_w := 'R';
    ELSIF ( ie_status_p = 'P' AND ie_status_unidade_w = 'R' ) THEN
        ie_reserved_w := 'R';
    ELSIF ( ie_status_p = 'F' AND ie_status_unidade_w = 'P' ) THEN
        ie_reserved_w := 'U';
    ELSIF ( ie_status_p = 'O' AND ie_status_unidade_w = 'R' ) THEN
        ie_reserved_w := 'R';
    ELSIF ( ie_status_p = 'R' AND ie_status_unidade_w = 'P' ) THEN
        ie_reserved_w := 'R';
    ELSIF ( ie_status_p = 'R' AND ie_status_unidade_w = 'G' ) THEN
        ie_reserved_w := 'R';
    END IF;

/* right click Generate Bed Reservation */

    IF ( ie_status_p = 'A' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'F' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'O' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'C' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'N' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'G';
    ELSIF ( ie_status_p = 'A' AND ie_status_unidade_w = 'P' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'A' AND ie_status_unidade_w = 'R' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'P' AND ie_status_unidade_w = 'L' ) THEN
        ie_reserved_w := 'A';
    ELSIF ( ie_status_p = 'A' AND ie_status_unidade_w = 'G' ) THEN
        ie_reserved_w := 'A';
    END IF;

    RETURN ie_reserved_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
