CREATE OR REPLACE FUNCTION get_bishop_scale_info ( nr_seq_bishop_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finality:
---------------------------------------------------------------------------------------------------
Direct calls:
[ X ]  Object dictionary [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Reports [ ] Others:
---------------------------------------------------------------------------------------------------
Attention points:
---------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255);


BEGIN
if (nr_seq_bishop_p IS NOT NULL AND nr_seq_bishop_p::text <> '') then
	if (ie_opcao_p = 'G') then
		select	substr(case
			when a.qt_pontuacao <= 5 then obter_desc_expressao(802369)
			when a.qt_pontuacao between 6 and 7 then obter_desc_expressao(802371)
			when a.qt_pontuacao >= 8 then obter_desc_expressao(802373)
			end,1,255)
		into STRICT	ds_retorno_w
		from	escala_bishop	a
		where	a.nr_sequencia	= nr_seq_bishop_p;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
