SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION get_blood_allocation_rule_3_ok ( nr_seq_reserva_p bigint, nr_seq_blood_p bigint, nm_usuario_p text ) RETURNS bigint AS $body$
DECLARE

    ie_retorno_w     bigint := 0;
    nr_seq_exame_w   bigint;
    ie_count_w       bigint := 0;

BEGIN
    nr_seq_exame_w := obter_parametro_funcao(450, 510, nm_usuario_p);
    SELECT
        COUNT(*)
    INTO STRICT ie_count_w
    FROM
        san_producao   s,
        san_derivado   d
    WHERE
        s.nr_seq_derivado = d.nr_sequencia
        AND d.ie_tipo_derivado = 'CH'
        AND s.nr_sequencia = nr_seq_blood_p;

    IF ( ie_count_w > 0 ) THEN
        SELECT
            COUNT(c.nr_seq_exame)
        INTO STRICT ie_retorno_w
        FROM
            san_reserva           a,
            san_exame_lote        b,
            san_exame_realizado   c
        WHERE
            a.nr_sequencia = nr_seq_reserva_p
            AND a.nr_sequencia = b.nr_seq_reserva
            AND c.nr_seq_exame_lote = b.nr_sequencia
            AND c.nr_seq_exame = nr_seq_exame_w
            AND c.ds_resultado = 'Positivo';

    END IF;

    RETURN ie_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
