CREATE OR REPLACE FUNCTION get_blood_allocation_rule_4_ok (nr_seq_reserva_p bigint, cd_pessoa_fisica_p text, ie_fator_rh_p text, dt_realizado_p timestamp, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


ie_retorno_w  bigint := -1;
nr_seq_exame_w  bigint;
ie_patient_age_w bigint := 0;
dt_realizado_w timestamp;


BEGIN

ie_patient_age_w := obter_idade_pf(cd_pessoa_fisica_p, clock_timestamp(), 'M');

if (ie_fator_rh_p = 'CH' and ie_patient_age_w > 3) then
    nr_seq_exame_w := obter_parametro_funcao(450, 510, nm_usuario_p);
    dt_realizado_w := dt_realizado_p - 7;

	select count(c.NR_SEQ_EXAME)
	into STRICT     ie_retorno_w
	from san_exame_lote b, san_exame_realizado c
	where b.nr_seq_reserva = nr_seq_reserva_p
	and c.nr_seq_exame_lote = b.nr_sequencia
	and c.nr_seq_exame = nr_seq_exame_w
	and c.dt_realizado between dt_realizado_w and dt_realizado_p
	and (c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '');

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
