CREATE OR REPLACE FUNCTION get_blood_product_status (nr_seq_cpoe_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_status_return_w varchar(1) := 'X';


BEGIN
	if (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '') then

		select	max(sr.ie_status)
		into STRICT	ds_status_return_w
		from	san_reserva sr,
			prescr_solic_bco_sangue psbs,
			cpoe_hemoterapia cph
		where	cph.nr_sequencia = nr_seq_cpoe_p
		and	psbs.nr_prescricao = sr.nr_prescricao
		and	cph.nr_sequencia = psbs.nr_seq_hemo_cpoe
		and	(sr.dt_liberacao_solicitacao IS NOT NULL AND sr.dt_liberacao_solicitacao::text <> '');

		if (ds_status_return_w <> 'L') then
			ds_status_return_w := 'X';
		end if;

	end if;
	
return ds_status_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
