CREATE OR REPLACE FUNCTION get_build_custom_branch (BRANCH_NAME_P text) RETURNS bigint AS $body$
DECLARE


build_tag_w man_custom_branch.cd_build_tag%type;


BEGIN
  select cd_build_tag
  into STRICT build_tag_w
  from man_custom_branch 
 where branch_name = branch_name_p;

return (lpad(SUBSTR(build_tag_w, instr(build_tag_w,'.',-1)+1, length(build_tag_w)),3,'0'))::numeric;

END;
