CREATE OR REPLACE FUNCTION get_category_vat ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, cd_convenio_p atend_categoria_convenio.cd_convenio%type, cd_categoria_p atend_categoria_convenio.cd_categoria%type) RETURNS bigint AS $body$
DECLARE

									
nr_seq_categoria_iva_w	pessoa_titular_convenio.nr_seq_categoria_iva%type;


BEGIN

nr_seq_categoria_iva_w := null;

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select  max(ptc.nr_seq_categoria_iva)
		into STRICT	nr_seq_categoria_iva_w
		from    pessoa_titular_convenio ptc
		where   ptc.cd_pessoa_fisica = cd_pessoa_fisica_p
		and   	ptc.cd_categoria = cd_categoria_p
		and   	ptc.cd_convenio = cd_convenio_p;
	end if;

	if (coalesce(nr_seq_categoria_iva_w::text, '') = '') then
		select	max(cc.nr_seq_categoria_iva)
		into STRICT 	nr_seq_categoria_iva_w
		from 	categoria_convenio cc
		where 	cc.cd_convenio = cd_convenio_p
		and 	cc.cd_categoria = cd_categoria_p;
	end if;

    return  nr_seq_categoria_iva_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
