CREATE OR REPLACE FUNCTION get_child_wdbp (nr_node_sequence bigint) RETURNS varchar AS $body$
DECLARE

    
islastwdbp	varchar(2) := 'N';
nr_temp_seq	bigint;
nr_node_sequence_w	bigint := nr_node_sequence;
ds_return	varchar(100) := null;


BEGIN

	while islastwdbp <> 'S' loop
		select	nr_sequencia
		into STRICT	nr_temp_seq
		from	objeto_schematic
		where	nr_seq_obj_sup = nr_node_sequence_w;

		nr_node_sequence_w := nr_temp_seq;

		islastwdbp := is_wdbp(nr_temp_seq);

	end loop;
	
	begin
		select	nm_tabela || ',' || nr_seq_visao
		into STRICT	ds_return
		from	objeto_schematic
		where	nr_sequencia = 	nr_temp_seq;
	
	exception
		when no_data_found or too_many_rows then
			ds_return := null;
	end;
	
	return ds_return;
end;
