CREATE OR REPLACE FUNCTION get_clin_path_evaluation ( nr_seq_resultado_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_avaliacao_w PROTOCOLO_INTEGRADO_AVALIA.DS_AVALIACAO%type;
ds_avaliacao_result_w PROTOCOLO_INTEGRADO_ETAPA.DS_AVALIACAO_RESULTADO%type;

c01 CURSOR FOR
SELECT 	ds_avaliacao
from	protocolo_integrado_avalia
where	nr_seq_resultado = nr_seq_resultado_p;


BEGIN

	open c01;
		loop
			fetch c01 into
				ds_avaliacao_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
			
			if (ds_avaliacao_w IS NOT NULL AND ds_avaliacao_w::text <> '') then
				ds_avaliacao_result_w := ds_avaliacao_result_w || ds_avaliacao_w || chr(10);
			end if;
		end loop;
	close c01;
	
	return ds_avaliacao_result_w;

end;
$body$
